mapper.select:

		select * from(
                select rownum rn, tt.* from(
                select * from eventboard_play where startDate <= sysdate and endDate >= sysdate order by startDate desc)tt
             )where rn >= ${startNum} and rn <= ${endNum}


select * from(
        select rownum rn, tt.* from(
        select * from eventboard where startDate <= sysdate and endDate >= sysdate order by startDate desc)tt
     )where rn >= ${startNum} and rn <= ${endNum}

select * from(
       select rownum rn, tt.* from(
       select * from eventboard_play where startDate > (select sysdate from dual) and startDate <= (select add_months(sysdate,3) from dual) order by startDate desc)tt
    )where rn >= ${startNum} and rn <= ${endNum}


select * from(
        select rownum rn, tt.* from(
        select * from eventboard_play where startDate >= (select add_months(sysdate,-3) from dual) and endDate <= (select (sysdate+1) as nextdate from dual) order by startDate desc)tt
     )where rn >= ${startNum} and rn <= ${endNum}

select * from(
        select rownum rn, tt.* from(
        select * from eventboard where startDate > (select sysdate from dual) and startDate <= (select add_months(sysdate,3) from dual) order by startDate desc)tt
     )where rn >= ${startNum} and rn <= ${endNum}

		select * from(
                select rownum rn, tt.* from(
                select * from eventboard where startDate >= (select add_months(sysdate,-3) from dual) and endDate <= (select (sysdate+1) as nextdate from dual) order by startDate desc)tt
             )where rn >= ${startNum} and rn <= ${endNum}
