mapper.select:
select * from(
select rownum rn, tt.* from(
select * from eventboard_play order by seq desc)tt
)where rn  >=  #{startNum} and rn<= #{endNum}

		select * from(
                select rownum rn, tt.* from(
                select * from eventboard where startDate >= #{selMonth} and endDate <= (select last_day(#{selMonth}) from dual) order by startDate desc)tt
             )where rn >= ${startNum} and rn <= ${endNum}
